/* script.min.js */
let currentIndex=0,slider=document.getElementById("reviewSlider"),cards=slider.querySelectorAll(".review-card"),visibleCards=window.innerWidth<=768?1:3,totalItems=cards.length,maxIndex=totalItems-visibleCards;function updateSlider(){const e=cards[0].offsetWidth+20;slider.style.transform=`translateX(-${currentIndex*e}px)`}function nextReview(){currentIndex=currentIndex+1>maxIndex?0:currentIndex+1,updateSlider()}function prevReview(){currentIndex=currentIndex-1<0?maxIndex:currentIndex-1,updateSlider()}setInterval(nextReview,1e4),window.addEventListener("resize",()=>{currentIndex=0,updateSlider()}),document.getElementById("contact-form").addEventListener("submit",function(e){e.preventDefault();const t=e.target,a=new FormData(t);document.getElementById("form-loading").style.display="block",fetch("https://script.google.com/macros/s/AKfycbzqGgtsC0CVronLdfb_v5ZizRkrbwiB4g_XJ71HEblEqm7WpLRdV4zPAB8RpimBnkHkYA/exec",{method:"POST",body:a}).then(e=>{e.ok&&(document.getElementById("form-response").style.display="block",document.getElementById("form-loading").style.display="none",t.reset())})});
